sp_widget.client_script:
function($scope) {
  var c = this;

  // Options for the dropdowns
  c.homeTypes = ['New home', 'Old home', 'Apartment', 'Condo'];
  c.roomOptions = ['1 room', '2 rooms', '3 rooms', '4 rooms'];
  c.priceRanges = ['$100,000 - $300,000', '$300,000 - $500,000', '$500,000 - $700,000', '$700,000 - $1,000,000'];

  c.selectedOption = 'buy'; // Default selected option

  c.selectOption = function(option) {
    c.selectedOption = option;
  };

  c.showOnMap = function() {
    // Logic to show the map based on the location
    alert("Showing map for: " + c.location);
  };

  c.searchProperties = function() {
    // Logic to handle search functionality
    alert("Searching properties for: " + c.homeType + ", " + c.rooms + ", " + c.priceRange + " in " + c.location);
  };
}

